#include<bits/stdc++.h>using namespace std; int main(){	#ifndef ONLINE_JUDGE	freopen("input.txt" , "r" , stdin);	freopen("output.txt" , "w" , stdout);	#endif     string s;    cin>>s;    long long int best=1,sum=1;    long long int l=s.length();    for(int i=0;i<l-1;i++){	if(s[i]==s[i+1]){		sum+=1;		best=max(best,sum);		continue;	} 	else{				sum=1;		continue;	}}if(best==1 && sum>1){	cout<<sum;}elsecout<<best; 	return 0;}