#include <bits/stdc++.h>using namespace std; #define lli long long intint main(){    ios_base::sync_with_stdio(false);cin.tie(NULL);     #ifndef ONLINE_JUDGE    freopen("input.txt", "r" , stdin);    freopen("outit.txt", "w", stdout);    #endif    int t=1;    // cin>>t;    while(t--){        lli n;        cin>>n;        std::vector<lli> v(n);        if(n==1)cout<<1;        else if(n==2 || n==3)cout<<"NO SOLUTION";        else{            if(n&1){                for(int i=n;i>=1;i-=2)cout<<i<<" ";                for(int i=n-1;i>=2;i-=2)cout<<i<<" ";            }            else{                for(int i=n-1;i>=1;i-=2)cout<<i<<" ";                for(int i=n;i>=2;i-=2)cout<<i<<" ";            }        }        cout<<"\n";    }}