#include <bits/stdc++.h>using namespace std; #define lli long long intint main(){    ios_base::sync_with_stdio(false);cin.tie(NULL);     #ifndef ONLINE_JUDGE    freopen("input.txt", "r" , stdin);    freopen("outit.txt", "w", stdout);    #endif    int t=1;    cin>>t;    while(t--){        lli n,x,y;        cin>>x>>y;        if(x<=y){            if(y&1){                n=y*y-x+1;            }            else{                y-=1;                n=y*y+x;            }        }        else{            if(x&1){                x-=1;                n=x*x+y;            }            else{                n=x*x-y+1;            }        }        cout<<n;        cout<<"\n";    }}