#include<bits/stdc++.h>using namespace std;int main(){	// #ifndef ONLINE_JUDGE	// freopen("input.txt" , "r" , stdin);	// freopen("output.txt" , "w" , stdout);	// #endif   long long int n;    cin>>n;	vector<int> v;    for(int i=0;i<n-1;i++){    	int x;    	cin>>x;    	v.push_back(x);    }    sort(v.begin(), v.end());    if(v[0]!=1){    	cout<<"1";    	return 0;    }    else if(v[n-2]!=n){    	cout<<n;    	return 0;    }    else{    for(int i=0;i<n-2;i++){    	if(v[i]+1==v[i+1])    		continue;    	else{    		cout<<(v[i]+1);    		break;    	}    }    }	return 0;}