#include <bits/stdc++.h>using namespace std; #define lli long long intint main(){    ios_base::sync_with_stdio(false);cin.tie(NULL);     #ifndef ONLINE_JUDGE    freopen("input.txt", "r" , stdin);    freopen("output.txt", "w", stdout);    #endif    int t=1;    // cin>>t;    while(t--){        lli n;        cin>>n;        if(n<3)cout<<"NO";        else if(!(n&1) && (n/2)&1)cout<<"NO";        else if(n&1 && ((n+1)/2)&1)cout<<"NO";        else{            cout<<"YES\n";            std::vector<lli> v1,v2;            if(n&1){                v1.push_back(1);v1.push_back(n-1);v2.push_back(n);                lli s2,s3,s4,i=2,j=n-2;                lli s1=s2=s3=s4=(n-3)/4;                while(s1--){v1.push_back(i);i++;}                while(s2--){v1.push_back(j);j--;}                while(s3--){v2.push_back(i);i++;}                while(s4--){v2.push_back(j);j--;}            }            else{                lli s1,s2,s3,s4;                s1=s2=s3=s4=n/4;                lli i=1,j=n;                while(s1--){v1.push_back(i);i++;}                while(s2--){v1.push_back(j);j--;}                while(s3--){v2.push_back(i);i++;}                while(s4--){v2.push_back(j);j--;}            }            cout<<v1.size()<<"\n";            for(lli i=0;i<v1.size();i++)cout<<v1[i]<<" ";            cout<<"\n";            cout<<v2.size()<<"\n";            for(lli i=0;i<v2.size();i++)cout<<v2[i]<<" ";        }        // cout<<"\n";    }}