#include <bits/stdc++.h>using namespace std; #define lli long long intint main(){    ios_base::sync_with_stdio(false);cin.tie(NULL);     #ifndef ONLINE_JUDGE    freopen("input.txt", "r" , stdin);    freopen("outit.txt", "w", stdout);    #endif    int t=1;    // cin>>t;    while(t--){        lli n,c=0;        cin>>n;        std::vector<int> v(n);        for(int i=0;i<n;i++)cin>>v[i];        for(int i=1;i<n;i++){            if(v[i]<v[i-1]){c+=v[i-1]-v[i];v[i]=v[i-1];}        }        cout<<c;        cout<<"\n";    }}