#include <bits/stdc++.h>using namespace std; #define lli long long intint main(){    ios_base::sync_with_stdio(false);cin.tie(NULL);     #ifndef ONLINE_JUDGE    freopen("input.txt", "r" , stdin);    freopen("output.txt", "w", stdout);    #endif    int t=1;    // cin>>t;    while(t--){        int n,x;cin>>n>>x;vector<int>c(n),p(n);        for(int i=0;i<n;i++)cin>>c[i];        for(int i=0;i<n;i++)cin>>p[i];        int dp[n+1][x+1];        for(int i=0;i<n+1;i++){          for(int j=0;j<x+1;j++){            if(i==0 || j==0)dp[i][j]=0;            else if(c[i-1]<=j){              dp[i][j]=max(p[i-1]+dp[i-1][j-c[i-1]],dp[i-1][j]);            }            else dp[i][j]=dp[i-1][j];          }        }        cout<<dp[n][x];        cout<<"\n";    }}