#include <bits/stdc++.h>using namespace std; #define lli long long int set<string>out;string s;int l;void solve(int n){    if(n==l){        string str="";        for(int i=0;i<l;i++)str+=s[i];        if(out.find(str)==out.end())out.insert(str);        return;    }    for(int i=n;i<l;i++){        swap(s[i],s[n]);        solve(n+1);        swap(s[i],s[n]);    }}int main(){    ios_base::sync_with_stdio(false);cin.tie(NULL);     #ifndef ONLINE_JUDGE    freopen("input.txt", "r" , stdin);    freopen("output.txt", "w", stdout);    #endif    lli t=1;    // cin>>t;    while(t--){        cin>>s;        l=s.length();        solve(0);        cout<<out.size()<<"\n";        for(auto it:out){            cout<<it<<"\n";        }    }}