#include <bits/stdc++.h>using namespace std; #define lli long long intint main(){    ios_base::sync_with_stdio(false);cin.tie(NULL);     #ifndef ONLINE_JUDGE    freopen("input.txt", "r" , stdin);    freopen("output.txt", "w", stdout);    #endif    int t=1;    // cin>>t;    while(t--){        int n;cin>>n;        std::vector<int> dp(n+1,1e9);        dp[0]=0;        for(int i=1;i<n+1;i++){          string s=to_string(i);          for(int j=0;j<s.length();j++){            dp[i]=min(dp[i],1+dp[i-(s[j]-48)]);          }        }        cout<<dp[n];        cout<<"\n";    }}