#include <bits/stdc++.h>using namespace std;#define modulo 1000000007#define int long long signed main(){    ios_base::sync_with_stdio(false);cin.tie(NULL);     #ifndef ONLINE_JUDGE    freopen("input.txt", "r" , stdin);    freopen("output.txt", "w", stdout);    #endif    int t=1;    // cin>>t;    while(t--){        string s1,s2;cin>>s1>>s2;        int l1=s1.length(),l2=s2.length();        int dp[l1+1][l2+1];        dp[0][0]=0;        for(int i=1;i<=l1;i++)dp[i][0]=1+dp[i-1][0];        for(int i=1;i<=l2;i++)dp[0][i]=1+dp[0][i-1];        for(int i=1;i<=l1;i++){            for(int j=1;j<=l2;j++){                if(s1[i-1]==s2[j-1])dp[i][j]=dp[i-1][j-1];                else dp[i][j]=min(1+dp[i-1][j-1],1+min(dp[i-1][j],dp[i][j-1]));            }        }        cout<<dp[l1][l2];    }}