#include <bits/stdc++.h>using namespace std; #define mod 10000007#define int long long #define N 200001int c[N];std::vector<int> adj[N];void dfs(int s, int e){//curr node, prev node    c[s]=1;    for(auto u:adj[s]){        if(u==e)continue;        dfs(u,s);        c[s]+=c[u];    }}signed main(){    ios_base::sync_with_stdio(false);cin.tie(NULL);     #ifndef ONLINE_JUDGE    freopen("input.txt", "r" , stdin);    freopen("output.txt", "w", stdout);    #endif    int t=1;    // cin>>t;    while(t--){        int n;cin>>n;        int emp[n-1];        for(int i=0;i<n-1;i++)cin>>emp[i];        int e=2;        for(int i=0;i<n-1;i++){            adj[emp[i]].push_back(e);            e++;        }        dfs(1,0);        for(int i=1;i<=n;i++)cout<<c[i]-1<<" ";    }}