#include <bits/stdc++.h>using namespace std; #define lli long long int int main(){    ios_base::sync_with_stdio(false);cin.tie(NULL);     #ifndef ONLINE_JUDGE    freopen("input.txt", "r" , stdin);    freopen("output.txt", "w", stdout);    #endif    lli t=1;    // cin>>t;    while(t--){        string s,w="",r="";        cin>>s;        sort(s.begin(), s.end());        lli n=s.length();        if(s[0]==s[n-1]){cout<<s;return 0;}        std::vector<int> v(26);        for(int i=0;i<n;i++)v[s[i]-65]++;        int odd=0,ind=-1;        for(int i=0;i<26;i++)if(v[i]&1){odd++;ind=i;}        if(odd>1)cout<<"NO SOLUTION";        else{            for(int i=0;i<26;i++){                if(ind!=i && v[i]>0){                    lli half=v[i]/2;                    while(half--)w+=i+65;                    v[i]/=2;                }            }            r=w;            if(ind!=-1){while(v[ind]--)w+=ind+65;}            reverse(r.begin(), r.end());            cout<<w+r;        }    }}